    public static function ImportPrices($items)
    {
        // /backend/catalog/one/prices/

        foreach ($items as $key => $item) {
            $val = Element::find()->select('price')->where(['code_1c' => $items[$key]->code])->asArray()->limit(1)->one();
            $items[$key]->price_control = $val['price'];
        }


        //Курсы валют НБРБ
        $currencies = self::getCurrencies();

        $mail_new_element = '';
        foreach ($items as $item) {

            if ($item->currency != 'BYN') {
                $item->price = self::calcPrice($item->price, $item->currency, $currencies);
                $item->price_old = self::calcPrice($item->price_old, $item->currency, $currencies);
            }

            $p = Element::findOne(['code_1c' => $item->code]);
            if (count($p) > 0) {
                Element::updateAll(
                    [
                        'price_control' => $item->price_control,
                        'price' => $item->price,
                        'price_1c' => $item->price,
                        'price_old' => $item->price_old,
                        'in_status' => $item->status,
                        'halva' => $item->halva,
                        'in_stock' => $item->in_stock,
                    ],
                    'code_1c = :code_1c',
                    [
                        ':code_1c' => $item->code
                    ]
                );
            } else {
                $p = new Element();
                $p->code_1c = $item->code;
                $p->article = $item->code;
                $p->alias = $item->code;
                $p->price = $item->price;
                $p->price_1c = $item->price;
                $p->price_old = $item->price_old;
                $p->halva = $item->halva;
                $p->in_stock = $item->in_stock;
                $p->published = 0;
                $p->title = $item->code;
                $p->id_category = 422;
                $p->id_manufacturer = 53;
                $p->id_measurement = 3;
                $p->save();

                if (count($p->errors) > 0) {
                    $mail_new_element .= 'Ошибка добавления товара, код 1С: ' . $item->code . '<br/>';
                } else {
                    $mail_new_element .= 'Добавлен товар, код 1С: ' . $item->code . '<br/>';
                }

            }
        }


        $val_price_control = 0.05;
        $text_mail = '<table border="1">
            <tr>
                <th>Артикул</th> 
                <th>Новая цена</th>
                <th>Прежнее значение цены</th>
                <th>Разница,%</th>
            </tr>';
        foreach ($items as $item) {
            if (!empty($item->price_control)) {
                $val = round($item->price / $item->price_control, 3);
                if ((abs(1 - $val)) > $val_price_control) {
                    $metka = 1;
                    $text_mail .=
                        '<tr>
                    <td>' . $item->code . '</td>
                    <td';
                    if ((1-$val)>0) $text_mail .= ' bgcolor="red"';
                    $text_mail .='>' . $item->price / 100 . '</td>
                    <td>' . $item->price_control / 100 . '</td>
                    <td>' . abs($val - 1) * 100 . '</td>
                </tr>';
                }
            }
        }
        $text_mail .= '</table>';

        if (!empty($mail_new_element)) {
            $mail_new_element = 'Добавлены следующие артикулы товаров:<br/>' . $mail_new_element . '<hr><br/>';
        }

        if (!$metka) {
            $text_mail = 'Новые цены отличаются от контрольных цен не более чем на ' . $val_price_control * 100 . '%.<br />Всё OK!';
        }
        Mail::mail(Yii::$app->params['adminEmail'], ['php@shop.by', 'rusrsk@kranik.by', 'poverennova@kranik.by'], 'Отчёт по обновлению цен и товаров на сайте shop.by', $mail_new_element . $text_mail);

        var_dump($mail_new_element . $text_mail);

        $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");///////////////
        fwrite($file, date('d.m.Y H:i:s') . ' -------ImportPrices . $items -------- ' . serialize($items) . PHP_EOL);/////////////////
        fwrite($file, date('d.m.Y H:i:s') . ' -------ImportPrices . count($items) -------- ' . count($items) . PHP_EOL);/////////////////
        fclose($file);////////////////////

    }

    public static function getCurrencies()
    {
        $curl = "http://www.nbrb.by/Services/XmlExRates.aspx?ondate=" . date("m/d/Y");
        $xml = file_get_contents($curl);
        $document = \phpQuery::newDocumentXML($xml);
        $currency = [];
        foreach (pq($document)->find('Currency') as $c) {
            $code = pq($c)->find('CharCode')->html();
            $rate = pq($c)->find('Rate')->html();
            if ($code == "USD") $currency["USD"] = $rate;
            if ($code == "EUR") $currency["EUR"] = $rate;
            if ($code == "RUB") $currency["RUB"] = $rate;
        }
        return $currency;
    }

    public static function calcPrice($price, $currency, $currencies)
    {
        $res = 0;
        if ($currency == "USD") $res = $currencies["USD"] * $price;
        if ($currency == "EUR") $res = $currencies["EUR"] * $price;
        if ($currency == "RUB") $res = $currencies["RUB"] * $price;
        if ($currency == "BYN") $res = $price;
        return $res;
    }

    public static function getOrders()
    {

        $status = Yii::$app->request->get('status');

        $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");//////////////////////////////
        fwrite($file, date('d.m.Y H:i:s') . ' ---getOrders . $status--- ' . serialize($status) . PHP_EOL);///////////////////////
        fclose($file);/////////////////

        if (!in_array($status, range(0, 10))) {
            return [
                'error' => true,
                'message' => 'Неверный статус',
                'code' => 100,
            ];
        } else {

            $orders = (new Query)
//              ->select('o.id as order, o.data, o.one_data as products, status as status, o.delivery as delivery, p.firmName as firm, p.ynp as ynp, p.user_id')
                ->select('o.id as order, o.one_data as products, status as status, o.delivery as delivery, p.firmName as firm, p.ynp as ynp, p.user_id')
                ->from('{{%shop_order}} o')
                ->leftJoin('{{%profiles}} p', 'o.id_user = p.user_id')
                ->where('o.status = :status', [':status' => $status])
                ->all();

            $ids = [];
            foreach ($orders as $order) {
                $items = unserialize($order['products']);
                foreach ($items as $item) {
                    $ids[] = $item;
                }
            }
            $ids = array_column($ids, 'id');
            $ids = array_unique($ids);
            $ids = array_diff($ids, [null]);

            $products = (new Query)
                ->select('id, id_category, id_category_1c, code_1c, price')
                ->from('{{%catalog_element}}')
                ->where(['in', 'id', $ids])
                ->all();

            foreach ($orders as $key => $order) {

                $delivery = unserialize($orders[$key]['delivery']);
                $orders[$key]['deliveryTime'] = $delivery['time'];
                $orders[$key]['deliveryType'] = $delivery['type'];
                $orders[$key]['deliveryCity'] = $delivery['city'];
                unset($orders[$key]['delivery']);

                if ($delivery['type'] == 1) {
                    $deliveryAddress = $orders['0']['data'];
                    $deliveryAddress = explode('<p><b>Адрес доставки: </b>', $deliveryAddress);
                    $deliveryAddress = explode('</p>', $deliveryAddress['1']);
                    $deliveryAddress = $deliveryAddress['0'];
                    $orders[$key]['deliveryAddress'] = $deliveryAddress;
                }

                $discounts = UserDiscount1c::getUserDiscount1c($order['user_id']);
                $items = unserialize($order['products']);
                $new = [];
                foreach ($items as $item) {
                    foreach ($products as $product) {

                        $arr_id_category_1c = json_decode($product['id_category_1c']);
                        if (is_array($arr_id_category_1c)) {
                            foreach ($arr_id_category_1c as $id_category_1c) {
                                if (array_key_exists($id_category_1c, $discounts)) {
                                    $product['price'] = round($product['price'] / 100 * (100 - $discounts[$id_category_1c]), 0);
                                }
                            }
                        }

                        if ($item['id'] == $product['id']) {
                            $new[] = [
                                'code_1c' => $product['code_1c'],
//                              'price' => (array_key_exists($product['id_category_1c'], $discounts)) ? (string)round($product['price'] / 100 * (100 - $discounts[$product['id_category_1c']]), 0) : $product['price'],
                                'price' => $product['price'],
                                'count' => $item['count'],
                            ];
                        }
                    }
                }
                $orders[$key]['products'] = $new;

            }

            if (count($orders)) Mail::mailInfo('1C. 1С получила новые заказы', json_encode($orders));


            if (count($orders)) {
                $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");//////////////////////////////
                fwrite($file, date('d.m.Y H:i:s') . ' ---getOrders . $orders--- ' . serialize($orders) . PHP_EOL);///////////////////////
                fclose($file);/////////////////
            }

            return $orders;
        }
    }

