    public static function updateDiscount()
    {

        $request = Yii::$app->getRequest()->post();


        $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");//////////////////////////////
        fwrite($file, date('H:i:s') . '----updateDiscount . $request ---------- ' . serialize($request) . PHP_EOL);//////////////////
        fclose($file);//////////////////////////


        $error = false;
        $error_msg = '';

        //Проверка есть ли такой юзер
        $userId = Profile::find()->select('user_id')->where('ynp = :ynp', [':ynp' => '100029184'])->asArray()->one()['user_id'];
        if ($userId === null) {
            $error = true;
            $error_msg = 'Пользователь с УНП: ' . $request['client_ynp'] . ' не найден';
            Mail::mailError('1C. Ошибка назначения скидки клиенту', $error_msg);
        }

        // Проверка наскидку
        if (empty($request['discount']) || $request['discount'] == '') {
            $error = true;
            $error_msg = 'Не указана скидка';
            Mail::mailError('1C. Ошибка назначения скидки клиенту', $error_msg);
        }

        // Удалить все скидки
        UserDiscount1c::deleteAll('id_user = :id_user', [':id_user' => $userId]);

        // Получить все категории
        $categories = Category::find()
            ->select('c1.id, c1.id_parent, c1.title')
            ->from('{{%catalog_category}} c1')
            ->leftJoin('{{%catalog_category}} c2', 'c1.id = c2.id_parent')
            ->where('c2.id IS NULL')
            ->asArray()->all();

        $categories = array_column($categories, 'id');

        // Создать скидки на все категории
        foreach ($categories as $category) {
            $UserDiscount = new UserDiscount1c;
            $UserDiscount->id_user = $userId;
            $UserDiscount->id_category = $category;
            $UserDiscount->discount = $request['discount'];
            $UserDiscount->save();
        }

        Mail::mailInfo('1C. Пользователю с УНП ' . $request['client_ynp'] . ' назначена скидка ' . $request['discount'] . '%', '');

        return [
            'msg' => $error_msg,
            'error' => $error,
        ];
    }

