        $productArticles = array_column($products, 'code');
        $productData = Element::find()->select('id, title, code_1c, in_stock')->where(['IN', 'code_1c', $productArticles])->asArray()->all();

        $orderOld = Order::find()->select('data')->where('id = :id', [':id' => $orderId])->asArray()->one()['data'];
        $html = explode('<table', $orderOld)['0'];

        $html .= '
            <table  border="1">
                <thead>
                <tr>
                    <th>№</th>
                    <th>Наименование</th>
                    <th>Количество</th>
                    <th>Цена с НДС, шт</th>
                    <th>Цена с НДС, итого</th>
                </tr>';

        foreach ($products as $product) {
            $now = [];

            foreach ($productData as $item) {
                if ($product['code'] == $item['code_1c']) {
                    $now = $item;
                    // Товар есть в 1С, но нет на сайте. Оповестим.
                    if ($item['title'] == '') Mail::mailInfo('1C. На сайте отсутствует товар с кодом: ' . $product['code']);
                    // Отминусуем наличие на складе
                    $in_stock = $item['in_stock'] - $product['count'];
                    Element::updateAll(['in_stock' => $in_stock], 'id = :id', [':id' => $item['id']]);
                    break;
                }
            }
            $html .= '
                    <tr>
                        <td>' . $now['id'] . '</td>
                        <td>' . $now['title'] . '</td>
                        <td>' . $product['count'] . '</td>
                        <td>' . Helper::formatPrice($product['price']) . '</td>
                        <td>' . Helper::formatPrice($product['price'] * $product['count']) . '</td>
                        <td>' . Helper::formatPrice(round(round(round(\common\modules\shop\models\frontend\Order::comma($product['price']) / 1.2, 2) * $product['count'], 2) * 1.2, 2)) . '</td>
                    </tr>';
        }
        $html .= '
                <tr style="text-align: right;">
                    <th colspan="5">Итого: ' . Helper::formatPrice($priceTotal) . '</th>
                </tr>
            </table>
        ';

        Order::updateAll(['data' => $html], 'id = :id', [':id' => $orderId]);
        \common\modules\shop\models\frontend\Order::mail($html, 'Заказ изменён');
        \common\modules\shop\models\frontend\Order::clientMail($html, 'Ваш заказ изменён');
    }

    public static function login()
