    public static function getTovs()
    {
        $order_id = Yii::$app->request->post('order_id');
        $products = explode(',', Yii::$app->request->post('products'));

        $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");//////////////////////////////
        fwrite($file, date('H:i:s') . '----getTovs . $order_id ---------- ' . serialize($order_id) . PHP_EOL);//////////////////
        fwrite($file, date('H:i:s') . '----getTovs . $products ---------- ' . serialize($products) . PHP_EOL);//////////////////
        fclose($file);//////////////////////////


        if (!empty(($order_id) OR ($products))) {

            $order = (new Query)
                ->select('o.data')
                ->from('{{%shop_order}} o')
                ->where('o.id = :id', [':id' => $order_id])
                ->one();

            $string = explode('</tr>', $order['data']);
            foreach ($products as $item) {
                foreach ($string as $key => $str) {
                    if (stripos($str, '<td>' . $item . '</td>')) {
                        $string[$key] = str_replace('<tr>', '<tr style="background-color: #fbb4b4;">', $str);
                    }
                }
            }
            $string = implode('</tr>', $string);

            Order::updateAll(['data' => $string], 'id = :id', [':id' => $order_id]);
        }
    }

    public static function getChangeInStock()
    {

        $order = json_decode(Yii::$app->getRequest()->post('data'), true);


        $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");//////////////////////////////
        fwrite($file, date('H:i:s') . '----getChangeInStock . $order ---------- ' . serialize($order) . PHP_EOL);//////////////////
        fclose($file);//////////////////////////

        if (!empty($order)) {
            $productArticles = array_keys($order['products']);
            $productData = Element::find()->select('id, code_1c, in_stock')->where(['IN', 'code_1c', $productArticles])->asArray()->all();

            foreach ($productData as $item) {
                if (($order['type']) == 1) {
                    $in_stock = $item['in_stock'] + $order['products'][$item['code_1c']];
                } else {
                    $in_stock = $item['in_stock'] - $order['products'][$item['code_1c']];
                    if ($in_stock < 0) {
                        $in_stock = 0;
                    }
                }
                Element::updateAll(['in_stock' => $in_stock], 'id = :id', [':id' => $item['id']]);
            }
        }
    }

