    public static function getChangeInvoice()
    {
        $request = json_decode(Yii::$app->getRequest()->post('data'), true);

        $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");//////////////////////////////
        fwrite($file, date('H:i:s') . '----getChangeInvoice . $request ---------- ' . serialize($request) . PHP_EOL);//////////////////
        fclose($file);//////////////////////////

        if (!empty($request)) {
            $order = (new Query)
                ->select('o.data')
                ->from('{{%shop_order}} o')
                ->where('o.id = :id', [':id' => $request['order_id']])
                ->one();

            $string = $order['data'];
            $string = substr($string, 0, strpos($string, '</tr>'));

            $content = '</tr>' . PHP_EOL;

            foreach ($request['products'] as $product) {
                $content .= '<tr>' . PHP_EOL;
                $content .= '    <td>' . $product['code_1c'] . '</td>' . PHP_EOL;
                $content .= '    <td>' . $product['title'] . '</td>' . PHP_EOL;
                $content .= '    <td>' . $product['count'] . '</td>' . PHP_EOL;
                $content .= '    <td>' . str_replace('.', ' р. ', $product['price']) . ' к.</td>' . PHP_EOL;
                $content .= '    <td>' . str_replace('.', ' р. ', $product['priceAll']) . ' к.</td>' . PHP_EOL;
                $content .= '</tr>' . PHP_EOL;
            }
            $content .= '<tr style="text-align: right;">' . PHP_EOL;
            $content .= '    <td colspan="5">Итого: ' . str_replace('.', ' р. ', $request['sum']) . ' к.</td>' . PHP_EOL;
            $content .= '</tr>' . PHP_EOL;

            $content .= '</table>' . PHP_EOL;

            $string .= $content;
        }

        $file = fopen($_SERVER['DOCUMENT_ROOT'] . "/counter.txt", "a");
        fwrite($file, date('H:i:s') . ' ' . serialize(Yii::$app->request->post()) . PHP_EOL);
        fwrite($file, $string . PHP_EOL);
        fclose($file);
        print_r($string);

        Order::updateAll(['data' => $string, 'cost' => $request['sum'] * 100], 'id = :id', [':id' => $request['order_id']]);
        \common\modules\shop\models\frontend\Order::mail($string, 'Заказ изменён');
        \common\modules\shop\models\frontend\Order::clientMail($string, 'Ваш заказ изменён');
    }


