    public static function getNewCard()
    {
//https://kranik.by/backend/catalog/one/new-card/?login=1C&password=hG7rFDm95D
//status_email = 0 -> отказ от рассылки
//status_email = 1 -> согласие на рассылку
//status_email = 10 -> отказ от рассылки - обработано 1С
//status_email = 11 -> согласие на рассылку - обработано 1С
//
//ответ 1С
//https://kranik.by/backend/catalog/one/new-card-status/
//[
//card = 1231231231231;
//status_id = 10 или 11;
//]


        $users = (new Query)
            ->select('p.card, p.surname, p.name, p.patronymic, p.birthday, p.phone_mobile, u.email, u.status_id')
            ->from('{{%users}} u')
            ->leftJoin('{{%profiles}} p', 'u.id = p.user_id')
//            ->where('u.status_id = :status', [':status' => 2])
//            ->where('(4 = u.status_id) OR (5 = u.status_id)')
            ->where('(0 = u.status_email) || (1 = u.status_email)')
            ->all();

        return $users;
    }

